require('./directionalnavigation.min.js');
/**
 * The Navigation class provides utilities for dealing with user requests
 * to nevigate around or away from the interactive controls.
 */
export class Navigation {
    constructor(rpc) {
        this.rpc = rpc;
        this.escapeKeys = {
            menu: false,
            view: false,
        };
        this.handlingExit = false;
        window.addEventListener('keydown', (ev) => {
            this.handleKeydown(ev);
        }, true);
        window.addEventListener('keyup', (ev) => {
            this.handleKeyup(ev);
        }, true);
        rpc.expose('keyboardShowing', () => {
            window.TVJS.DirectionalNavigation.enabled = false;
        });
        rpc.expose('keyboardHiding', () => {
            window.TVJS.DirectionalNavigation.enabled = true;
        });
        rpc.expose('focusIn', () => {
            const firstFocus = document.querySelector('[tabindex="0"]') || document.body;
            firstFocus.focus();
        });
    }
    /**
     * Should be called when the integration wants to intercept an event which
     * would otherwise cause the Interactive integration to close, such as
     * the "X" button on the user's controller when watching on their Xbox.
     * Calling this will cause the next press of "X" to have no effect.
     */
    handleExit() {
        this.handlingExit = true;
    }
    /**
     * Handle exiting via escape and Game.
     * @private
     * @param {KeyboardEvent} ev
     */
    handleKeydown(ev) {
        if (this.handlingExit && ev.keyCode === 196 /* GamepadB */) {
            ev.preventDefault();
            ev.stopPropagation();
            this.handlingExit = false;
            return;
        }
        if (ev.keyCode === 207 /* Menu */ || ev.keyCode === 208 /* View */) {
            ev.keyCode === 207 /* Menu */ ? (this.escapeKeys.menu = true) : (this.escapeKeys.view = true);
        }
        else {
            this.escapeKeys.menu = false;
            this.escapeKeys.view = false;
        }
        if (ev.keyCode === 27 /* Escape */ ||
            ev.keyCode === 196 /* GamepadB */ ||
            (this.escapeKeys.menu && this.escapeKeys.view)) {
            ev.preventDefault();
            ev.stopPropagation();
            this.escapeKeys.menu = false;
            this.escapeKeys.view = false;
            this.rpc.call('focusOut', {}, false);
            return;
        }
        if (ev.keyCode === 13 /* Enter */ || ev.keyCode === 195 /* GamepadA */) {
            this.handleSubmit();
            return;
        }
        this.rpc.call('navigate', {}, false);
    }
    /**
     * Handle exiting via escape and Game.
     * @private
     * @param {KeyboardEvent} ev
     */
    handleKeyup(ev) {
        if (ev.keyCode === 207 /* Menu */) {
            this.escapeKeys.menu = false;
        }
        if (ev.keyCode === 208 /* View */) {
            this.escapeKeys.view = false;
        }
    }
    handleSubmit() {
        const clickEvent = document.createEvent('MouseEvents');
        clickEvent.initEvent('mousedown', true, true);
        const currentEl = document.activeElement;
        if (currentEl) {
            currentEl.dispatchEvent(clickEvent);
        }
    }
}

import { EventEmitter } from 'eventemitter3';
// interface IInteractiveRPCReply<T> extends IRPCReply<T> {} // tslint:disable-line
/**
 * The socket wraps the RPC instance and provides an event emitter than
 * fires when various Interactive events come in. These events correspond
 * with the `onSomething...` methods as documented in the [protocol
 * specification](https://dev.mixer.com/reference/interactive/protocol/protocol.pdf).
 * The payload of the event corresponds with the `params` from the events
 * that the Interactive service sends. For example:
 *
 * ```js
 * mixer.socket.on('onReady', data => {
 *  console.log('Are the controls ready?', data.isReady);
 * });
 * mixer.socket.on('onSceneCreate', scene => {
 *   // do something with the newly created scene...
 * });
 * ```
 */
export class Socket extends EventEmitter {
    constructor(rpc) {
        super();
        this.rpc = rpc;
        rpc.expose('recieveInteractivePacket', (data) => {
            this.emit('interactivePacket', data);
            this.emit(data.method, data.params);
        });
    }
    /**
     * Sets the handler to use when the CDK requests a dump of the current
     * controls state. It should return metadata about the scenes. This will
     * help in your debugging, but you do not have to implement it.
     * @param {function(): IStateDump} fn
     */
    dumpHandler(fn) {
        this.rpc.expose('dumpState', fn);
    }
    on(event, handler) {
        super.on(event, handler);
        return this;
    }
    /**
     * Makes a call to the Interactive service. This is just like sending a call
     * over the [Interactive protocol](https://dev.mixer.com/reference/interactive/protocol/protocol.pdf),
     * however the only available methods are `giveInput` and `getTime`.
     *
     * For example:
     *
     * ```js
     * // All giveInput calls must contain at minimum the event and control ID.
     * // Everything else is passed through to the game client verbatim.
     * mixer.socket.call('giveInput', {
     *   event: 'click',
     *   controlID: 'my_button',
     *   moreCustomData: true,
     * });
     * ```
     *
     * @param {string} method
     * @param {*} params
     * @param {boolean} [waitForReply=true]
     * @return {Promise.<object> | undefined} If waitForReply is true, a
     * promise is returned that resolves once the server responds.
     */
    call(method, params, waitForReply = true) {
        const reply = this.rpc.call('sendInteractivePacket', {
            method,
            params,
        }, waitForReply);
        if (!waitForReply) {
            return;
        }
        return reply;
    }
}

