import { Clock } from './bundle/clock';
import { Display } from './bundle/display';
import { Navigation } from './bundle/navigation';
import { Socket } from './bundle/socket';
import { RPC } from './rpc';
import './doc';
export * from './rpc';
export * from './typings';
export * from './decoration';
const rpc = new RPC(window.top, '1.0');
/**
 * Returns the fully qualified URL to a static project asset.
 * You can pass multiple path segments to join them together.
 * @param {...string} path
 * @returns {string}
 */
export function asset(...path) {
    // For now this is fairly stub-ish, it serves as an injection point if we
    // decide to change how assets are delivered in the future.
    return `./${path.map(segment => segment.replace(/^\/+|\/+$/, '')).join('/')}`;
}
let isReady = false;
/**
 * resendReady is called by the host when it detects the iframe loads. It seems
 * that loading can be fired "slowly" in some cases causing the isLoaded event
 * to fire off before the host sets up listeners, so that host will call this
 * method when it first boots to make sure that it didn't miss anything!
 */
rpc.expose('resendReady', () => {
    if (isReady) {
        rpc.call('controlsReady', {}, false);
    }
});
/**
 * This signals to Mixer that the controls have been bound and are ready to
 * start taking Interactive calls. You need to call this to allow your
 * controls to be displayed.
 *
 * In the Preact starter, this is called automatically by the MState class.
 */
export function isLoaded() {
    isReady = true;
    rpc.call('controlsReady', {}, false);
}
window.addEventListener('beforeunload', () => {
    rpc.call('unloading', {}, false);
});
/**
 * Logs an exception to Mixer, called by the exported `log` object.
 * @param {string} level
 * @param {any[]} params
 */
function captureLogMessage(level, params) {
    rpc.call('log', {
        level,
        params: params.map(param => {
            if (param instanceof Error) {
                return {
                    message: param.message,
                    stack: param.stack,
                    metadata: typeof param.cause === 'function' ? param.cause() : null,
                };
            }
            return param;
        }),
    }, false);
}
/**
 * `log` has methods to capture messages from your controls. These'll be
 * exposed in the miix UI, and we will continue to build further telemetry
 * around them.
 */
export const log = {
    debug(...params) {
        captureLogMessage('debug', params);
    },
    info(...params) {
        captureLogMessage('info', params);
    },
    warn(...params) {
        captureLogMessage('warn', params);
    },
    error(...params) {
        captureLogMessage('error', params);
    },
};
window.onerror = (_message, _source, _lineno, _colno, error) => {
    log.error('An uncaught exception occurred.', error);
};
/**
 * Since your interactive controls can be run by any client, it's sometimes
 * useful (particularly if you do fancier service integration) to be able to
 * verify that the player is who they say they are. This method provides a
 * means for you to do that. This is what happens:
 *
 *  1. You create a cryptographically secure challenge for the user. This MUST
 *     be done on your service; the challenge is used so that adversaries
 *     cannot impersonate users (for instance, by gathering challenge
 *     responses for their own integrations then injecting
 *     those into your controls).
 *
 *  2. Mixer servers will create a token based on the challenge and a
 *     secret, and return that in the response to this method.
 *
 *  3. You may transmit the token and challenge to your services and call
 *     /api/v1/interactive/identity/verify to get the user ID that corresponds
 *     to the token. The API will return a 400 if the challenge is invalid.
 *
 * Visualized, that's something like this:
 *
 * ```
 *
 *     challenge                  
 *                                            
 *                                                   
 *    Your                                                   
 *  Service                  Controls                        
 *                                                           
 *                 token                                     
 *                              Mixer 
 *                                       
 *                                                              
 *                                                              
 *           POST /interactive/identity/verify                  
 *              
 *             { "challenge":"...","token:"..." }                
 *                                                        
 * ```
 *
 * @param {string} challenge
 * @returns {Promise.<string>}
 */
export function getIdentityVerification(challenge) {
    return rpc.call('verificationChallenge', { challenge }, true);
}
/**
 * Your project's package configuration.
 * @type {IPackageConfig}
 */
export const packageConfig = window.mixerPackageConfig;
/**
 * The list of available locales. This is populated when the build is run
 * with the cdk-webpack-plugin and contains the files in the configured locale
 * folder. For example, this might be set to `['en', 'es']` if you have English
 * and Spanish translation files.
 * @type {Array.<string>}
 */
export const locales = window.mixerLocales;
/**
 * Singleton {@link Socket} instance.
 * @type {Socket}
 */
export const socket = new Socket(rpc);
/**
 * Singleton {@link Display} instance.
 * @type {Display}
 */
export const display = new Display(rpc);
/**
 * Singleton {@link Navigation} instance.
 * @type {Navigation}
 */
export const navigation = new Navigation(rpc);
/**
 * Singleton {@link Clock} instance.
 * @type {Clock}
 */
export const clock = new Clock(socket);

/**
 * InputKinds are passed into the @Input() decorator to define what data
 * type the inputs takes. If you're using TypeScript and you define the
 * types of your properties, we can automatically infer some of these
 * except for the ones marked "not inferrable".
 *
 * For example:
 *
 * ```
 * @Control({ name: 'button' })
 * class Button {
 *   @Input()
 *   public dimensions: Mixer.IDimensions;
 *
 *   @Input({ kind: Mixer.InputKind.Color })
 *   public background: string;
 * }
 * ```
 */
export var InputKind;
(function (InputKind) {
    InputKind[InputKind["Dimensions"] = 0] = "Dimensions";
    InputKind[InputKind["Number"] = 1] = "Number";
    InputKind[InputKind["String"] = 2] = "String";
    InputKind[InputKind["Boolean"] = 3] = "Boolean";
    InputKind[InputKind["Color"] = 4] = "Color";
    InputKind[InputKind["Duration"] = 5] = "Duration";
    InputKind[InputKind["Url"] = 6] = "Url";
    InputKind[InputKind["JSON"] = 7] = "JSON";
})(InputKind || (InputKind = {}));
const sceneMetaKey = '__mix_scene';
const controlMetaKey = '__miix_control';
const descriptorMetaKey = '__miix_descriptor';
/**
 * The Registry is a simple class that maintains a list of available
 * controls and scenes, and can return them given control kinds or scene IDs.
 */
export class Registry {
    constructor() {
        this.scenes = Object.create(null);
        this.controls = Object.create(null);
        this.config = window.mixerPackageConfig;
    }
    /**
     * Adds a collection of controls and scenes to the registry. This will throw
     * if anything given is not a scene or control.
     * @param {...*} things
     * @returns {Registry}
     */
    register(...things) {
        things.forEach(thing => {
            if (thing[sceneMetaKey]) {
                this.registerScene(thing, thing[sceneMetaKey]);
            }
            else if (thing[controlMetaKey]) {
                this.registerControl(thing, thing[controlMetaKey]);
            }
            else {
                throw new Error(`Passed ${thing.name} to the miix registry, but it wasn't decorated with ` +
                    '@Control or @Scene!');
            }
        });
        return this;
    }
    /**
     * Returns the Control descriptor for a control of the given kind, or
     * thows if it's not found.
     * @param {string} kind
     * @returns {IControlDescriptor}
     * @throws {Error} if the control kind is not found
     */
    getControl(kind) {
        const control = this.controls[kind];
        if (!control) {
            throw new Error(`No control was found for kind "${kind}"! If you have a class already, ` +
                `make sure you're passing it to the Registry.register() function.`);
        }
        return control;
    }
    /**
     * Returns the Scene descriptor for the given scene ID, returning the
     * default scene if a specific handler wasn't found. Throws if no default
     * scene is present and the specific ID is not registered.
     * @param {string} id
     * @returns {ISceneDescriptor}
     * @throws {Error} if an appropriate scene implementation is not found
     */
    getScene(id) {
        const scene = this.scenes[id] || this.defaultScene;
        if (!scene) {
            throw new Error(`No scene class was found for scene ID "${id}" and no default scene was ` +
                `registered! If you have a default handler, make sure you pass it to the ` +
                `Registry.register() function, or create a specific class for this scene.`);
        }
        return scene;
    }
    /**
     * Returns inputs defined on the given control instance.
     * @param {object} control
     * @returns {IInputDescriptor[]}
     * @throws {Error} if the passed object is not decorated with @{@link Scene}
     * or @{@link Control}.
     */
    getInputs(control) {
        const descriptor = control.constructor[descriptorMetaKey];
        if (!descriptor) {
            throw new Error(`Tried to get inputs on ${control.constructor.name}, but it isn't a ` +
                `@Scene or @Control object!`);
        }
        return descriptor.inputs || [];
    }
    registerScene(scene, options) {
        const existing = options.id && this.scenes[options.id];
        const id = options.id || 'default';
        if (existing) {
            throw new Error(`Duplicate scene IDs registered! Both ${existing.ctor.name} and ` +
                `${scene.name} registered themselves for scene ID ${id}`);
        }
        const descriptor = Object.assign({}, options, this.config.scenes[id], { ctor: scene });
        Object.defineProperty(scene, descriptorMetaKey, { value: descriptor });
        this.scenes[id] = descriptor;
        if (options.default) {
            this.defaultScene = descriptor;
        }
    }
    registerControl(control, options) {
        const existing = options.kind && this.controls[options.kind];
        if (existing) {
            throw new Error(`Duplicate controls registered! Both ${existing.ctor.name} and ` +
                `${control.name} registered themselves for control kind ${options.kind}`);
        }
        const descriptor = Object.assign({}, options, this.config.controls[options.kind], { ctor: control });
        Object.defineProperty(control, descriptorMetaKey, { value: descriptor });
        this.controls[options.kind] = descriptor;
    }
}
/**
 * Scene is a decorator you can use to designate a class as a Scene. See
 * documentation on {@link ISceneOptions} for more info.
 * @param {ISceneOptions} [options]
 */
export function Scene(options = { default: true }) {
    return (ctor) => {
        Object.defineProperty(ctor, sceneMetaKey, { value: options });
    };
}
/**
 * Scene is a decorator you can use to designate a class as a Scene. See
 * documentation on {@link IControlOptions} for more info.
 * @param {IControlOptions} [options]
 */
export function Control(options) {
    return (ctor) => {
        Object.defineProperty(ctor, controlMetaKey, { value: options });
    };
}
/**
 * @Input decorates a property on a control. It makes it configurable in the
 * Interactive studio and settable for Preact components. See the
 * {@link IInputOptions} for more info.
 * @param {IInputOptions} [_options]
 */
export function Input(_options = {}) {
    return (_host, _propertyName) => {
        // noop, this is handled by static analysis
    };
}

properties.description:
Whether to use this scene as the handler for all scenes.

You can override scenes by their `id` to use a different scene for a
certain sceneID. In cases where there isn't a specific class for a
sceneID, the default will be used.

```
@Scene({ default: true })
class MyAwesomeScene {
  // ...
}
```

When specified, registers this class to handle a specific scene ID.
For instance, if you wanted the scene IOD `lobby` and `arena` to be
implemented with two different classes, you could do that with
something like the following:

```
@Scene({ id: 'lobby' })
class Lobbby {
  // ...
}

@Scene({ id: 'arena' })
class Arena {
  // ...
}
```

